import styled from "styled-components";

export const Button = styled.button`
  padding: 15px;
  background-color: #4282FA;
  color: white;
  border-radius: 10px;
  font-weight: bold;
  border: none;
  letter-spacing: 2px;
  cursor: pointer;
  @media only screen and (max-width: 480px) {
    margin-bottom: 20px;
  }
`;
